status_by_user:
CREATE TABLE `status_by_user` (
  `USER` char(32)  DEFAULT NULL -- 'User for which the status variable is reported.',
  `VARIABLE_NAME` varchar(64) NOT NULL -- 'Status variable name.',
  `VARIABLE_VALUE` varchar(1024) DEFAULT NULL -- 'Aggregated status variable value.'
) ;

setup_instruments:
CREATE TABLE `setup_instruments` (
  `NAME` varchar(128) NOT NULL -- 'Instrument name',
  `ENABLED` enum('YES','NO') NOT NULL -- 'Whether or not the instrument is enabled. It can be disabled, and the instrument will produce no events.',
  `TIMED` enum('YES','NO') NOT NULL -- 'Whether or not the instrument is timed. It can be set, but if disabled, events produced by the instrument will have NULL values for the corresponding TIMER_START, TIMER_END, and TIMER_WAIT values.'
) ;

accounts:
CREATE TABLE `accounts` (
  `USER` char(128)  DEFAULT NULL -- 'The connection''s client user name for the connection, or NULL if an internal thread.',
  `HOST` char(255)  DEFAULT NULL -- 'The connection client''s host name, or NULL if an internal thread.',
  `CURRENT_CONNECTIONS` bigint(20) NOT NULL -- 'Current connections for the account.',
  `TOTAL_CONNECTIONS` bigint(20) NOT NULL -- 'Total connections for the account.'
) ;

status_by_thread:
CREATE TABLE `status_by_thread` (
  `THREAD_ID` bigint(20) unsigned NOT NULL -- 'The thread identifier of the session in which the status variable is defined.',
  `VARIABLE_NAME` varchar(64) NOT NULL -- 'Status variable name.',
  `VARIABLE_VALUE` varchar(1024) DEFAULT NULL -- 'Aggregated status variable value.'
) ;

global_status:
CREATE TABLE `global_status` (
  `VARIABLE_NAME` varchar(64) NOT NULL -- 'The global status variable name.',
  `VARIABLE_VALUE` varchar(1024) DEFAULT NULL -- 'The global status variable value.'
) ;

performance_timers:
CREATE TABLE `performance_timers` (
  `TIMER_NAME` enum('CYCLE','NANOSECOND','MICROSECOND','MILLISECOND','TICK') NOT NULL -- 'Time name, used in the setup_timers table.',
  `TIMER_FREQUENCY` bigint(20) DEFAULT NULL -- 'Number of timer units per second. Dependent on the processor speed.',
  `TIMER_RESOLUTION` bigint(20) DEFAULT NULL -- 'Number of timer units by which timed values increase each time.',
  `TIMER_OVERHEAD` bigint(20) DEFAULT NULL -- 'Minimum timer overhead, determined during initialization by calling the timer 20 times and selecting the smallest value. Total overhead will be at least double this, as the timer is called at the beginning and end of each timed event.'
) ;

session_connect_attrs:
CREATE TABLE `session_connect_attrs` (
  `PROCESSLIST_ID` int(11) NOT NULL -- 'Session connection identifier.',
  `ATTR_NAME` varchar(32) NOT NULL -- 'Attribute name.',
  `ATTR_VALUE` varchar(1024) DEFAULT NULL -- 'Attribute value.',
  `ORDINAL_POSITION` int(11) DEFAULT NULL -- 'Order in which attribute was added to the connection attributes.'
) CHARSET=utf8mb3 COLLATE=utf8mb3_bin;

file_instances:
CREATE TABLE `file_instances` (
  `FILE_NAME` varchar(512) NOT NULL -- 'File name.',
  `EVENT_NAME` varchar(128) NOT NULL -- 'Instrument name associated with the file.',
  `OPEN_COUNT` int(10) unsigned NOT NULL -- 'Open handles on the file. A value of greater than zero means that the file is currently open.'
) ;

cond_instances:
CREATE TABLE `cond_instances` (
  `NAME` varchar(128) NOT NULL -- 'Client user name for the connection, or NULL if an internal thread.',
  `OBJECT_INSTANCE_BEGIN` bigint(20) unsigned NOT NULL -- 'Address in memory of the instrumented condition.'
) ;

users:
CREATE TABLE `users` (
  `USER` char(128)  DEFAULT NULL -- 'The connection''s client user name for the connection, or NULL if an internal thread.',
  `CURRENT_CONNECTIONS` bigint(20) NOT NULL -- 'Current connections for the user.',
  `TOTAL_CONNECTIONS` bigint(20) NOT NULL -- 'Total connections for the user.'
) ;

hosts:
CREATE TABLE `hosts` (
  `HOST` char(255)  DEFAULT NULL -- 'Host name used by the client to connect, NULL for internal threads or user sessions that failed to authenticate.',
  `CURRENT_CONNECTIONS` bigint(20) NOT NULL -- 'Current number of the host''s connections.',
  `TOTAL_CONNECTIONS` bigint(20) NOT NULL -- 'Total number of the host''s connections'
) ;

setup_consumers:
CREATE TABLE `setup_consumers` (
  `NAME` varchar(64) NOT NULL -- 'Consumer name',
  `ENABLED` enum('YES','NO') NOT NULL -- 'YES or NO for whether or not the consumer is enabled. You can modify this column to ensure that event information is added, or is not added.'
) ;

rwlock_instances:
CREATE TABLE `rwlock_instances` (
  `NAME` varchar(128) NOT NULL -- 'Instrument name associated with the read write lock',
  `OBJECT_INSTANCE_BEGIN` bigint(20) unsigned NOT NULL -- 'Address in memory of the instrumented lock',
  `WRITE_LOCKED_BY_THREAD_ID` bigint(20) unsigned DEFAULT NULL -- 'THREAD_ID of the locking thread if locked in write (exclusive) mode, otherwise NULL.',
  `READ_LOCKED_BY_COUNT` int(10) unsigned NOT NULL -- 'Count of current read locks held'
) ;

session_account_connect_attrs:
CREATE TABLE `session_account_connect_attrs` (
  `PROCESSLIST_ID` int(11) NOT NULL -- 'Session connection identifier.',
  `ATTR_NAME` varchar(32) NOT NULL -- 'Attribute name.',
  `ATTR_VALUE` varchar(1024) DEFAULT NULL -- 'Attribute value.',
  `ORDINAL_POSITION` int(11) DEFAULT NULL -- 'Order in which attribute was added to the connection attributes.'
) CHARSET=utf8mb3 COLLATE=utf8mb3_bin;

status_by_account:
CREATE TABLE `status_by_account` (
  `USER` char(32)  DEFAULT NULL -- 'User for which the status variable is reported.',
  `HOST` char(60)  DEFAULT NULL -- 'Host for which the status variable is reported.',
  `VARIABLE_NAME` varchar(64) NOT NULL -- 'Status variable name.',
  `VARIABLE_VALUE` varchar(1024) DEFAULT NULL -- 'Aggregated status variable value.'
) ;

user_variables_by_thread:
CREATE TABLE `user_variables_by_thread` (
  `THREAD_ID` bigint(20) unsigned NOT NULL -- 'The thread identifier of the session in which the variable is defined.',
  `VARIABLE_NAME` varchar(64) NOT NULL -- 'The variable name, without the leading @ character.',
  `VARIABLE_VALUE` longblob DEFAULT NULL -- 'The variable value'
) ;

replication_applier_status:
CREATE TABLE `replication_applier_status` (
  `CHANNEL_NAME` varchar(256) NOT NULL -- 'The replication channel name.',
  `SERVICE_STATE` enum('ON','OFF') NOT NULL -- 'Shows ON when the replication channel''s applier threads are active or idle, OFF means that the applier threads are not active.',
  `REMAINING_DELAY` int(10) unsigned DEFAULT NULL -- 'Seconds the replica needs to wait to reach the desired delay from master.',
  `COUNT_TRANSACTIONS_RETRIES` bigint(20) unsigned NOT NULL -- 'The number of retries that were made because the replication SQL thread failed to apply a transaction.'
) ;

mutex_instances:
CREATE TABLE `mutex_instances` (
  `NAME` varchar(128) NOT NULL -- 'Instrument name associated with the mutex.',
  `OBJECT_INSTANCE_BEGIN` bigint(20) unsigned NOT NULL -- 'Memory address of the instrumented mutex.',
  `LOCKED_BY_THREAD_ID` bigint(20) unsigned DEFAULT NULL -- 'The THREAD_ID of the locking thread if a thread has a mutex locked, otherwise NULL.'
) ;

setup_timers:
CREATE TABLE `setup_timers` (
  `NAME` varchar(64) NOT NULL -- 'Type of instrument the timer is used for.',
  `TIMER_NAME` enum('CYCLE','NANOSECOND','MICROSECOND','MILLISECOND','TICK') NOT NULL -- 'Timer applying to the instrument type. Can be modified.'
) ;

session_status:
CREATE TABLE `session_status` (
  `VARIABLE_NAME` varchar(64) NOT NULL -- 'The session status variable name.',
  `VARIABLE_VALUE` varchar(1024) DEFAULT NULL -- 'The session status variable value.'
) ;

status_by_host:
CREATE TABLE `status_by_host` (
  `HOST` char(60)  DEFAULT NULL -- 'Host for which the status variable is reported.',
  `VARIABLE_NAME` varchar(64) NOT NULL -- 'Status variable name.',
  `VARIABLE_VALUE` varchar(1024) DEFAULT NULL -- 'Aggregated status variable value.'
) ;

replication_applier_configuration:
CREATE TABLE `replication_applier_configuration` (
  `CHANNEL_NAME` varchar(256) NOT NULL -- 'Replication channel name.',
  `DESIRED_DELAY` int(11) NOT NULL -- 'Target number of seconds the replica should be delayed to the master.'
) ;
